s$cluster)\n\nclusplot(data[1:7],res$cluster, color = TRUE,\n         shade = TRUE, labels =3,lines=0,\n         main =\"Gráfico de Conglomerados\")\n\ninstall.packages(\"factoextra\")\nlibrary(factoextra)\nfviz_nbclust(data[,1:7], kmeans, method = \"silhouette\")","metadata":{"execution":{"iopub.status.busy":"2022-09-06T10:53:17.030248Z","iopub.execute_input":"2022-09-06T10:53:17.032628Z","iopub.status.idle":"2022-09-06T10:55:08.439343Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  PAM                                                  #\n#########################################################\n\nres <- pam(scale(data[1:7]),3)\nplot(res)\n\npar(mfrow=c(1,3))\nfor(h in 2:4){\n  res <- pam(scale(data[1:7]),h)\n  plot(res,which.plots=2)\n}\npar(mfrow=c(1,1))\n\nlibrary(factoextra)\nfviz_nbclust(data[,1:7], pam, method = \"silhouette\")\n\npamk(scale(data[1:7]),criterion=\"asw\")\n\nres <- pam(scale(data[1:7]),3)\nplotcluster(data[1:7],res$clustering)","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:00:02.07573Z","iopub.execute_input":"2022-09-06T11:00:02.077731Z","iopub.status.idle":"2022-09-06T11:00:03.642893Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  CLARA                                                #\n#########################################################\n\nres <- clara(scale(data[1:7]),3)\nplot(res)\n\npar(mfrow=c(1,3))\nfor(h in 2:4){\n  res <- clara(scale(data[1:7]),h)\n  plot(res,which.plots=2)\n}\npar(mfrow=c(1,1))\n\npamk(scale(data[2:7]),criterion=\"asw\",usepam=FALSE)\n\nres <- clara(scale(data[1:7]),3)\nplotcluster(data[1:7],res$clustering)","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:01:02.719643Z","iopub.execute_input":"2022-09-06T11:01:02.72316Z","iopub.status.idle":"2022-09-06T11:01:03.324221Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  FANNY                                                #\n#########################################################\n\nres <- fanny(scale(data[1:7]),3)\nplot(res)\n\npar(mfrow=c(1,3))\nfor(h in 2:4){\n  res=fanny(scale(data[1:7]),h)\n  plot(res,which.plots=2)\n}\npar(mfrow=c(1,1))","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:01:49.108356Z","iopub.execute_input":"2022-09-06T11:01:49.110364Z","iopub.status.idle":"2022-09-06T11:01:50.403388Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  CLUSTER JERÁRQUICO                                   #\n#########################################################\n\nhead(data)\nstr(data)\n\n#La función \"dist\" calcula una matriz de disimilaridad para las instancias\na <- hclust(dist(data[,1:7]))\nplot(a)\n\ndis <- dist(data[,1:7])\n\npar(mfrow=c(2,4))\na <- hclust(dis, method=\"single\")\nplot(a)\na <- hclust(dis, method=\"complete\")\nplot(a)\na <- hclust(dis, method=\"average\")\nplot(a)\na <- hclust(dis, method=\"centroid\")\nplot(a)\na <- hclust(dis, method=\"median\")\nplot(a)\na <- hclust(dis, method=\"mcquitty\")\nplot(a)\na <- hclust(dis, method=\"ward.D2\")\nplot(a)\npar(mfrow=c(1,1))\n\nSegmento <- cutree(a, k=3)\nhead(data.frame(data,Segmento))","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:03:02.87965Z","iopub.execute_input":"2022-09-06T11:03:02.882253Z","iopub.status.idle":"2022-09-06T11:03:03.334728Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  Clustering jerarquico aglomerativo usando Agnes      #\n#########################################################\n\nlibrary(cluster)\n\npar(mfrow=c(1,2))\na <- agnes(scale(data[,1:7]),metric=\"euclidean\",method=\"ward\")\nplot(a)\npar(mfrow=c(1,1))\n\npar(mfrow=c(2,2))\na <- agnes(scale(data[,1:7]),metric=\"euclidean\",method=\"ward\")\nplot(a)\na <- agnes(scale(data[,1:7]),metric=\"manhattan\",method=\"ward\")\nplot(a)\npar(mfrow=c(1,1))\n\ndiss.data <- daisy(scale(data[,1:7]))\nres <- agnes(scale(data[,1:7]),metric=\"manhattan\",method=\"ward\")\npar(mfrow=c(1,3))\nfor(h in 2:4){\n  conglomerados <- cutree(res,h)\n  plot(silhouette(conglomerados,diss.data))\n}\npar(mfrow=c(1,1))\n\nSegmento <- cutree(a,k=3)\nhead(data.frame(data,Segmento))","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:04:48.564452Z","iopub.execute_input":"2022-09-06T11:04:48.566583Z","iopub.status.idle":"2022-09-06T11:04:49.370221Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#########################################################\n#  Clustering jerarquico aglomerativo usando DIANA      #\n#########################################################\n\nlibrary(cluster)\n\npar(mfrow=c(1,2))\na <- diana(scale(data[,1:7]),metric=\"euclidean\")\nplot(a)\npar(mfrow=c(1,1))\n\npar(mfrow=c(2,2))\na <- diana(scale(data[,1:7]),metric=\"euclidean\")\nplot(a)\na <- diana(scale(data[,1:7]),metric=\"manhattan\")\nplot(a)\npar(mfrow=c(1,1))\n\ndiss.data <- daisy(scale(data[,1:7]))\nres <- diana(scale(data[,1:7]),metric=\"manhattan\")\npar(mfrow=c(1,3))\nfor(h in 2:4){\n  conglomerados <- cutree(res,h)\n  plot(silhouette(conglomerados,diss.data))\n}\npar(mfrow=c(1,1))\n\nSegmento <- cutree(a,k=3)\nhead(data.frame(data,Segmento))","metadata":{"execution":{"iopub.status.busy":"2022-09-06T11:05:58.327056Z","iopub.execute_input":"2022-09-06T11:05:58.328907Z","iopub.status.idle":"2022-09-06T11:05:59.097528Z"},"trusted":true},"execution_count":null,"outputs":[]}]}